import React, { useState } from 'react';
import {
  diet_type,
  health_restrictions,
  meal_type,
  dish_type,
  cuisine_type
  } from './data';
import Select from 'react-select';
import makeAnimated from 'react-select/animated';

function Home () {

  const INITIAL_STATE = {
    main_ingredient: '',
    diet_type: '',
    health_restrictions: '',
    cuisine_type: '',
    meal_type: '',
    dish_type: '',
    min_calories: '',
    max_calories: '',
    min_prep: '',
    max_prep: '',
    excluded: ''
  };

  const [formData, setFormData] = useState(INITIAL_STATE);

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData((data) => ({
      ...data,
      [name] : value
    }));
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    setFormData(INITIAL_STATE)
  };

  const animatedComponents = makeAnimated();

  return (
    <div classname="search">
      <form className="search-form" onSubmit={handleSubmit}>
        <div className="text-center">
          <label>Main Ingredient:</label>
            <input
              id='main_ingredient'
              type='text'
              name='main_ingredient'
              value={formData.main_ingredient}
              onChange={handleChange}
              required/>
        </div>
          <label>Main Ingredient:</label>
          <input
            id='main_ingredient'
            type='text'
            name='main_ingredient'
            value={formData.main_ingredient}
            onChange={handleChange}
            required/>

        <Select
          className="dt_dropdown"
          name="diet_type"
          components={animatedComponents}
          options={diet_type}
          closeMenuOnSelect={false}
          isMulti
        />

        <Select
          className="hr_dropdown"
          name="health_restrictions"
          components={animatedComponents}
          options={health_restrictions}
          closeMenuOnSelect={false}
          isMulti
        />

        <Select
          className="mt_dropdown"
          name="meal_type"
          components={animatedComponents}
          options={meal_type}
          closeMenuOnSelect={false}
          isMulti
        />

        <Select
          className="dt2_dropdown"
          name="dish_type"
          components={animatedComponents}
          options={dish_type}
          closeMenuOnSelect={false}
          isMulti
        />

        <Select
          className="ct_dropdown"
          name="cuisine_type"
          components={animatedComponents}
          options={cuisine_type}
          closeMenuOnSelect={false}
          isMulti
        />

        <label>Minimum Calories:</label>
          <input
            id='min_calories'
            type='integer'
            name='min_calories'
            value={formData.min_calories}
            onChange={handleChange}/>

        <label>Maximum Calories:</label>
          <input
            id='max_calories'
            type='integer'
            name='max_calories'
            value={formData.max_calories}
            onChange={handleChange}/>

        <label>Minimum Prep Time:</label>
          <input
            id='min_prep'
            type='integer'
            name='min_prep'
            value={formData.min_prep}
            onChange={handleChange}/>

        <label>Maximum Prep Time:</label>
          <input
            id='max_prep'
            type='integer'
            name='max_prep'
            value={formData.max_prep}
            onChange={handleChange}/>

        <label>Exclude:</label>
          <input
            id='exclusions'
            type='text'
            name='exculsions'
            value={formData.excluded}
            onChange={handleChange}/>

        <button className="search-button" onChange={handleSubmit}>Search</button>
      </form>
    </div>
  )
};

export default Home;
