    <div className="Recipes">
      <h1>{title}</h1>
      <p>{calories}</p>
      <img className={style.image} src={image} alt={title} />
      <ul>
        {ingredients.map((ingredient, index) => (
          <li key={index}>
            {ingredient.text}
          </li>
        ))}
      </ul>
      <a href="/">
        <button>Back to Home</button>
      </a>
    </div>
  )
}

