  health_restrictions,
  meal_type,
  dish_type,
  cuisine_type
  } from './data';
import Select from 'react-select';
import makeAnimated from 'react-select/animated';
import Recipe from './Recipe';

function Home () {

  const INITIAL_STATE = {
    main_ingredient: '',
    diet_type: '',
    health_restrictions: '',
    cuisine_type: '',
    meal_type: '',
    dish_type: '',
    min_calories: '',
    max_calories: '',
    min_prep: '',
    max_prep: '',
    excluded: ''
  };

