import React, { useState, useEffect } from 'react';
import { Link } from 'react-router-dom';
import Header from './Header';
import Footer from './Footer';
import {
  diet_type,
  health_restrictions,
  meal_type,
  dish_type,
  cuisine_type
  } from './data';
import Select from 'react-select';
import makeAnimated from 'react-select/animated';
import Recipe from './Recipe';

function Home () {

  const API_ID = process.env.REACT_APP_API_ID;
  const API_KEY = process.env.REACT_APP_API_KEY;

  const [recipes, setRecipes] = useState([]);
  const [search, setSearch] = useState('');
  const [query, setQuery] = useState('')


  useEffect(() => {
    const getRecipes = async () => {
      const resp = await fetch(
        `https://api.edamam.com/search?q=${query}&app_id=${API_ID}&app_key=${API_KEY}`);
      const data = await resp.json();
      setRecipes(data.hits);
    };
    getRecipes();
  }, [query, API_ID, API_KEY]);  

  const INITIAL_STATE = {
    main_ingredient: '',
    diet_type: '',
    health_restrictions: '',
    cuisine_type: '',
    meal_type: '',
    dish_type: '',
    min_calories: '',
    max_calories: '',
    min_prep: '',
    max_prep: '',
    excluded: ''
  };

  const [formData, setFormData] = useState(INITIAL_STATE);

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData((data) => ({
      ...data,
      [name] : value
    }));
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    setFormData(INITIAL_STATE)
  };

  const animatedComponents = makeAnimated();

  return (
    <div className="App">
      <Header />
        <div className="container">

          <form className="search-form" onSubmit={handleSubmit}>
            <div className="row">
              <label for="main_ingredient">Main Ingredient:</label>
              <input
                id='main_ingredient'
                type='text'
                name='main_ingredient'
                value={formData.main_ingredient}
                onChange={handleChange}
                required/>
            </div>

            <div className="row">
              <Select
                className="dt_dropdown"
                name="diet_type"
                components={animatedComponents}
                options={diet_type}
                closeMenuOnSelect={false}
                isMulti/>

              <Select
                className="hr_dropdown"
                name="health_restrictions"
                components={animatedComponents}
                options={health_restrictions}
                closeMenuOnSelect={false}
                isMulti/>
            </div>

            <div className="row">
              <Select
                className="mt_dropdown"
                name="meal_type"
                components={animatedComponents}
                options={meal_type}
                closeMenuOnSelect={false}
                isMulti/>

              <Select
                className="dt2_dropdown"
                name="dish_type"
                components={animatedComponents}
                options={dish_type}
                closeMenuOnSelect={false}
                isMulti/>

              <Select
                className="ct_dropdown"
                name="cuisine_type"
                components={animatedComponents}
                options={cuisine_type}
                closeMenuOnSelect={false}
                isMulti/>
            </div>

            <div className="row">
              <label>Minimum Calories:</label>
                <input
                  id='min_calories'
                  type='integer'
                  name='min_calories'
                  value={formData.min_calories}
                  onChange={handleChange}/>

              <label>Maximum Calories:</label>
                <input
                  id='max_calories'
                  type='integer'
                  name='max_calories'
                  value={formData.max_calories}
                  onChange={handleChange}/>
            </div>

            <div className="row">
              <label>Maximum Prep Time:</label>
                <input
                  id='max_prep'
                  type='integer'
                  name='max_prep'
                  value={formData.max_prep}
                  onChange={handleChange}/>

              <label>Maximum Prep Time:</label>
                <input
                  id='max_prep'
                  type='integer'
                  name='max_prep'
                  value={formData.max_prep}
                  onChange={handleChange}/>
            </div>

            <div className="row">
              <label>Exclude:</label>
                <input
                  id='exclusions'
                  type='text'
                  name='exculsions'
                  value={formData.excluded}
                  onChange={handleChange}/>
            </div>

            <div className="search-button">
              <input type="submit" value="Search">Search</input>
            </div>
          </form>

          <div className="recipes">
          {recipes.map((recipe, index) => {
            <Recipe
              key={index}
              title={recipe.recipe.label}
              calories={recipe.recipe.calories}
              image={recipe.recipe.image}
            />
          })}
          </div>
        </div>
      <Footer />
    </div>
  )
};

export default Home;
