import React, { useState } from 'react';
import {
  diet_type,
  health_restrictions,
  meal_type,
  dish_type,
  cuisine_type
  } from './data';
import Select from 'react-select';
import makeAnimated from 'react-select/animated';

function Home () {

  const API_ID = process.env.REACT_APP_API_ID
  const API_KEY = process.env.REACT_APP_API_KEY

  // const [recipes, setRecipes] = useState([]);
  // const [search, setSearch] = useState('');
  const [query, setQuery] = useState('')

  useEffect(() => {
    const getRecipes = async () => {
      const resp = await fetch(
        `https://api.edamam.com/search?q=${query}&app_id=${API_ID}&app_key=${API_KEY}`);
      const data = await resp.json();
      setRecipes(data.hits);
    };
    getRecipes();
  }, [query, API_ID, API_KEY]);  

  const INITIAL_STATE = {
    main_ingredient: '',
    diet_type: '',
    health_restrictions: '',
    cuisine_type: '',
    meal_type: '',
    dish_type: '',
    min_calories: '',
    max_calories: '',
    min_prep: '',
    max_prep: '',
    excluded: ''
  };

  const [formData, setFormData] = useState(INITIAL_STATE);

  const updateSearch = (e) => {
    const { name, value } = e.target;
    setFormData((data) => ({
      ...data,
      [name] : value
    }));
  };

  const getSearch = (e) => {
    e.preventDefault();
    setFormData(INITIAL_STATE)
  };

  const animatedComponents = makeAnimated();

  return (
    <div className="App">
    <div classname="home-search-form">
      <div className="container text-center">
        <h1 className="mb-4 font-weight-bold">The Feeder</h1>
          <p className="lead">A Simple Recipe Directory</p>
          <div>
            <img src="images/food_pic.png" alt="food_pic" width="400" height="200"/>
          </div>
      <form className="search-form" onSubmit={getSearch}>

        <div className="form-input">
          <label>Main Ingredient:</label>
          <input
            id='main_ingredient'
            type='text'
            name='main_ingredient'
            value={formData.main_ingredient}
            onChange={updateSearch}
            required/>
        </div>

        <div className="form-input">
          <Select
            className="dt_dropdown"
            name="diet_type"
            components={animatedComponents}
            options={diet_type}
            closeMenuOnSelect={false}
            isMulti/>
        </div>

        <div className="form-input">
          <Select
            className="hr_dropdown"
            name="health_restrictions"
            components={animatedComponents}
            options={health_restrictions}
            closeMenuOnSelect={false}
            isMulti/>
        </div>

        <div className="form-input">
          <Select
            className="mt_dropdown"
            name="meal_type"
            components={animatedComponents}
            options={meal_type}
            closeMenuOnSelect={false}
            isMulti/>
        </div>

        <div className="form-input">
          <Select
            className="dt2_dropdown"
            name="dish_type"
            components={animatedComponents}
            options={dish_type}
            closeMenuOnSelect={false}
            isMulti/>
        </div>

        <div className="form-input">
          <Select
            className="ct_dropdown"
            name="cuisine_type"
            components={animatedComponents}
            options={cuisine_type}
            closeMenuOnSelect={false}
            isMulti/>
        </div>

        <div className="form-input">
          <label>Minimum Calories:</label>
            <input
              id='min_calories'
              type='integer'
              name='min_calories'
              value={formData.min_calories}
              onChange={updateSearch}/>
        </div>

        <div className="form-input">
          <label>Maximum Calories:</label>
            <input
              id='max_calories'
              type='integer'
              name='max_calories'
              value={formData.max_calories}
              onChange={updateSearch}/>
        </div>

        <div className="form-input">
          <label>Minimum Prep Time:</label>
            <input
              id='min_prep'
              type='integer'
              name='min_prep'
              value={formData.min_prep}
              onChange={updateSearch}/>
        </div>

        <div className="form-input">
          <label>Maximum Prep Time:</label>
            <input
              id='max_prep'
              type='integer'
              name='max_prep'
              value={formData.max_prep}
              onChange={updateSearch}/>
        </div>

        <div className="form-input">
          <label>Exclude:</label>
            <input
              id='exclusions'
              type='text'
              name='exculsions'
              value={formData.excluded}
              onChange={updateSearch}/>
        </div>

        <button className="search-button" onChange={handleSubmit}>Search</button>
      </form>
      </div>
    </div>
    </div>
  )
};

export default Home;
