import React from 'react';


function Home() {
  const { currentUser } = useContext(UserContext);

  console.debug(
    "Homepage",
    "currentUser=",
    currentUser);

  return (
    <div className="Home">
      <div className="container text-center">
        <h1 className="mb-4 font-weight-bold">BroadKast</h1>
        <p className="lead">Video Streaming Platform</p>
        <div className="image-container">
          <img src="images/broadkast_main.jpg" alt="broadkast_image" width="400" height="200"/>
        </div>
        {currentUser
          ? <h2> 
            Welcome Back, {currentUser.firstName || currentUser.username}!
          </h2>
          : (
            <p>
              <Link className="btn btn-light font-weight-bold mr-3" to="/login">Log In</Link>
              <Link className="btn btn-light font-weight-bold mr-3" to="/signup">Sign Up</Link>
            </p>
        )}
      </div>
    </div>
  );
}

export default Home;
